mapper.select:
SELECT tel
FROM mbr
WHERE id = #{id}

mapper.update:
UPDATE board
SET view_cnt = view_cnt + 1
WHERE bno = #{bno}

mapper.select:
SELECT bno, writer , type, price , addrcode , addrdetail , addrgps ,  title , contents
FROM board
WHERE bno = #{bno}

mapper.update:
UPDATE board
SET type = #{type} , title = #{title} , contents = #{contents} , price = #{price}
	, addrcode = #{addrcode} , addrdetail = #{addrdetail} , addrgps = #{addrgps}
WHERE bno = #{bno}

update excercise
set tcode = #{type} , buy_type = #{buy_type}


	, limit_date = #{useday}


	, limit_cnt = #{useday}

	,"Update 오류"

where bno = #{bno}

UPDATE travel
SET tcode = #{type} , checkin_date = #{useday}
WHERE bno = #{bno}

UPDATE ticket
SET tcode = #{type} , ticket_date = #{useday}
WHERE bno = #{bno}

UPDATE etc
SET tcode = #{type} 
WHERE bno = #{bno}

mapper.delete:
DELETE FROM
excercise travel ticket etc
WHERE bno = #{bno}

mapper.update:
UPDATE board
set state = 1
WHERE bno = #{bno}
